import React, { Component } from 'react'
import { Link } from "react-router-dom";


export default class Header extends Component {
    constructor(props){
        super(props)

        this.state={
           
            home: false,
            add: true
          
        }

    

      this.handleToggle = () => {
            this.setState({ add: !this.state.add });
            this.setState({ home: !this.state.home });
          };

          
         
    }
    
 
   
    render() {
        return (
            <div >
                <header >

                    <nav className="navbar navbar-expand-md navbar-dark  p-3" style={{backgroundColor: "black"}}>
                        <div><h1 className="navbar-text hero-text h1 ">Book Store</h1></div>
                        <ul>
                           
                            <Link className={this.state.home ? "navbar-brand visible nav-element mt-n1" :"navbar-brand invisible nav-element" } onClick={this.handleToggle}   to={{pathname: '/'}}>Home</Link>
                            <Link className={this.state.add ? "navbar-brand visible nav-element mt-n1" :"navbar-brand invisible nav-element" } onClick={this.handleToggle}  to={{pathname: '/add-student'}}>add student</Link>
                          
                        </ul>

                    </nav>
                </header>
            </div>
        )
    }
}
