import React, { Component } from 'react'
import BookService from '../service/BookService'

 class BookComponent extends Component {
     constructor(props){
         super(props)


        this.state={

          book:[]
            

        }
        this.addBook=this.addBook.bind(this);
        this.editBook=this.editBook.bind(this);
     }

     editBook(id){

        this.props.history.push(`/update/${id}`);

    }

   deleteBook(id){
    BookService.DeleteBook(id).then((res)=>{

      console.log("ttt");
     });
   }

     componentDidMount(){
        BookService.getBook().then((res)=>{

            this.setState({book: res.data});

         });
     }
      addBook(){
       this.props.history.push('/add-employee');
      }

      
    
  
  
    render() {
        return (
            <div>
                <h2 className="text-center">Employee list</h2>

                <button className="btn btn-primary" onClick={this.addBook}>Add  Employee</button>

                <div className="row">
                    <table className="table table-striped table-bordered">

                        <thead>

                            <tr>
                                <th>First name</th>
                                <th>Last name</th>
                                <th>Email</th>
                                <th>Delete/Update</th>
                            </tr>

                        </thead>

                        <tbody>

                           {
                               this.state.book.map(
                                   book=>
                                   <tr key={book.id}>

                                       <td>{book.firstName}</td>
                                       <td>{book.lastName}</td>
                                       <td>{book.email}</td>
                                       <td><button onClick={()=>this.editBook(book.id)} className="btn btn-success " >update</button><button  onClick={()=>this.deleteBook(book.id)}  className="btn btn-danger " style={{marginLeft:"10px"}} >delete</button></td>
                                       
                                   </tr>
                               )
                           }

                        </tbody>

                    </table>
                </div>
                
            </div>
        )
    }
}

export default BookComponent;
